mapper.select:
select contentsid,title,introduction,region1,region2,address,roadaddress,latitude,longitude,postcode,phoneno,photo,views ,(select count(*) from content_like where content_id = #{sid} ) as likes
,(select count(*) from content_like where content_id = #{sid} and user_id = #{userid}) as like_true ,(select count(*) from content_favor where content_id = contentsid and user_id = #{userid}) as favor_true
from place where contentsid = #{sid}

mapper.update:
UPDATE place
SET views = views + 1
WHERE contentsid = #{sid}
